prerequisite.query:
DO $$
DECLARE msg text = $m$Please add the line "plv8.start_proc = 'xt.js_init'" to your postgresql.conf and restart the database server.$m$;
BEGIN
  IF NOT (current_setting('plv8.start_proc') = 'xt.js_init') THEN
    raise exception '%', msg;
  END IF;
  EXCEPTION WHEN sqlstate '42704' THEN
    RAISE EXCEPTION '%', msg;
END;
$$ LANGUAGE plpgsql;
SELECT 1;

SELECT BOOL_AND(cmnttype_id IS NOT NULL)
                  FROM cmnttypesource
                  LEFT OUTER JOIN cmnttype ON cmnttypesource_cmnttype_id = cmnttype_id;

SELECT BOOL_AND(source_id IS NOT NULL)
                  FROM cmnttypesource
                  LEFT OUTER JOIN source ON cmnttypesource_source_id = source_id;
