-- Set our path to xwd so inserts work without xwd.foo notation.
SET search_path = public, pg_catalog;

-- Set sequence values.
SELECT pg_catalog.setval('custtype_custtype_id_seq', 21, true);
SELECT pg_catalog.setval('addr_addr_id_seq', 53, true);
SELECT pg_catalog.setval('cntct_cntct_id_seq', 42, true);
SELECT pg_catalog.setval('cntcteml_cntcteml_id_seq', 27, true);
SELECT pg_catalog.setval('vend_vend_id_seq', 101, true);
SELECT pg_catalog.setval('cust_cust_id_seq', 126, true);
SELECT pg_catalog.setval('custform_custform_id_seq', 9, true);
SELECT pg_catalog.setval('crmacct_crmacct_id_seq', 49, true);
SELECT pg_catalog.setval('shipto_shipto_id_seq', 37, true);

-- Disable triggers so foreign keys don't complain until we're done.
ALTER TABLE cntct DISABLE TRIGGER ALL;
ALTER TABLE vendinfo DISABLE TRIGGER ALL;
ALTER TABLE custinfo DISABLE TRIGGER ALL;
ALTER TABLE crmacct DISABLE TRIGGER ALL;










-- Enable triggers again so foreign keys will be checked now that we're done.
ALTER TABLE cntct ENABLE TRIGGER ALL;
ALTER TABLE vendinfo ENABLE TRIGGER ALL;
ALTER TABLE custinfo ENABLE TRIGGER ALL;
ALTER TABLE crmacct ENABLE TRIGGER ALL;
